      <div class="tabs__content">



        <div class="content">
            <info v-show="activeTab === 1" @iscorrect="checkTab1" />
              <seats v-show="activeTab === 2" class="seats" @iscorrect="checkTab2"/>
        </div>
        



        <div class="switcher">
             <div class="btn-container">
     <submitButton v-if="activeTab > 1" color="light-green" :title="$t('buttons.previous')" @click="prevTab" />
            </div>
            <div class="btn-container">
     <submitButton v-if="activeTab < 2" color="green" :disabledClass="isDisabled ? 'disabled' : ''"  :title="$t('buttons.next')" @click="nextTab" :isDisabled="isDisabled"/>
            </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import submitButton from "~/components/shared/submitButton";
import info from "~/components/forms/addHall/info/";
import seats from "~/components/general/seats/";
export default {
    data(){
        return {
            activeTab: 1,
            tabs: {
                tab1: false,
                tab2: false,
            },
            hall_info: {
                hall_name: "",
                hall_description: "",
            },
            locked_seats: [],
            corridors: [],
        }
    },
    components: {
        submitButton,
        info,
        seats
    }, 
    methods: {
        nextTab(){
            if (this.activeTab != 3){
                 this.activeTab ++;
            }
        },
         prevTab(){
            if (this.activeTab != 0){
                 this.activeTab --;
            }
        },
        checkTab1(data){
            if (data === false){
                this.tabs.tab1 = false;
            }else {
            this.hall_info.hall_name = data.hall_name;
            this.hall_info.hall_description = data.hall_description;
            this.tabs.tab1 = true;
            }
        },
        checkTab2(data){
            this.hall_info.rows_number = data.rowsNumber;
            this.hall_info.columns_number = data.columnsNumber;
            this.locked_seats = data.lockedSeats;
            // creating an array contain the columnCorridors and rowCorridors: 
            let rowCorridors = data.rowCorridors.map(corridor_number=> {
                return {
                corridor_number: corridor_number,
                direction: "row"
                }
            });
            let columnCorridors = data.columnCorridors.map(corridor_number=> {
                return {
                corridor_number: corridor_number,
                direction: "column"
                }
            });
            this.corridors = [...columnCorridors,...rowCorridors];
            this.tabs.tab2 = true;
        },
    },
     computed:{
    language(){
      return this.$store.getters.getLocale;
    },
    isDisabled(){
        const tab = this.activeTab;
        let status = true;
        switch (tab) {
            case 1:
                this.tabs.tab1 ? status = false : status = true
                break;
            case 2:
                this.tabs.tab2 ? status = false : status = true
                break;
             case 3:
                this.tabs.tab3 ? status = false : status = true
                break;
            default:
                break;
        }
        return status
    }
  }
};
</script>

